import React from 'react';
import './App.css';
import {forceNumber, label} from './utils';
import RangeStepInput from './RangeStepInput.js';
import App from './PyScr.js';

export default class App2 extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: 50 };
        this.onChange2 = this.onChange2.bind(this)
    }

    render() {
        return <div className="App">
            <div className={this.props.div}>
                <RangeStepInput
                    min={1} max={100}
                    value={this.state.value} step={1}
                    id={this.props.div}
                    className="inputRange"
                    onChange={this.onChange2}
                />
                <div className={this.props.div}>
                    <span>{label(this.state.value, this.props.lab)}</span>
                </div>
                <div className={this.props.div}>
                    <span>{this.props.div}={this.state.value}</span>
                </div>
            </div>
            <App lbl={this.props.div} val={this.state.value} />
        </div>;

    };
    onChange2(e) {
        const newVal = forceNumber(e.target.value);
        this.setState({value: newVal});
    }
};
