import React from 'react';
import './App.css';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: "Not Clicked" };
    }
    render() {
        return <div className="App">
            <div className="Test">
                <button onClick={this.onClick.bind(this)}>Mint</button>
                <div className="Test">
                    <span>{this.state.value}</span>
                </div>
            </div>
        </div>;
    };
    onClick(e) {
      this.setState({value: "Yeah"});
      const express = require('express');
      this.setState({value: "Yeah2"});
      const {spawn} = require('child_process');
      this.setState({value: "Yeah3"});
      const app = express();
      const port = 3000;
      this.setState({value: "Yeah4"});
      app.get('/', (req, res) => {
        this.setState({value: "Yeah5"});
        const py = spawn('python', ['test.py']);
        this.setState({value: "Yeah6"});
        py.stdout.on('data', (data) => {
          this.setState({value: "STDOUT"});
          console.log(`stdout: ${data}`);
        });
        py.stderr.on('data', (data) => {
          this.setState({value: "STDERR"});
          console.log(`stderr: ${data}`);
        });
        py.on('close', (code) => {
          this.setState({value: "CLOSE"});
          console.log(`closed with: ${code}`);
        });
      })
      app.listen(port, () => console.log(`Example app listening on port ${port}!`))
    }
};
